<?require($_SERVER["DOCUMENT_ROOT"]."/bitrix/modules/main/include/prolog_before.php");if (CModule::IncludeModule("iblock")) {	 $arr_help=Array("PROPERTY_CKECKLIST_YUR"=>31);	 $arr=explode("*",$_GET["value"]);	 $name=$arr[0];	 $id=$arr[1];	 $checked=($_GET["checked"]=="true")?1:0;	 $value=intval($arr[2]);	 $db_props = CIBlockElement::GetProperty($arr_help[$name], str_replace("E","",$id), array("sort" => "asc", "id" => "asc"),array("CODE"=>str_replace("PROPERTY_","",$name)));				$arr_t=Array();				while($ar_props = $db_props->Fetch())				{					print_r($ar_props);					if($ar_props["VALUE"]!=$value)						$arr_t[]=array("VALUE"=>$ar_props["VALUE"],"DESCRIPTION"=>"");									}				if($checked)					$arr_t[]=array("VALUE"=>$value,"DESCRIPTION"=>"");			print_r($arr_t);		CIBlockElement::SetPropertyValues($id, $arr_help[$name], $arr_t, str_replace("PROPERTY_","",$name));	}	require($_SERVER["DOCUMENT_ROOT"]."/bitrix/modules/main/include/epilog_after.php");?>